CREATE TABLE IF NOT EXISTS tbCustomers (coId INTEGER PRIMARY KEY AUTOINCREMENT, coName TEXT NOT NULL, coStreet TEXT, coPostcode TEXT, coCity TEXT, coDistance REAL, coDescription TEXT, coDeleted INTEGER DEFAULT 0 NOT NULL);
CREATE TABLE IF NOT EXISTS tbTravelAllowance (coId INTEGER PRIMARY KEY AUTOINCREMENT, coRate REAL NOT NULL, coValidFrom TEXT NOT NULL, coInvalidFrom TEXT NOT NULL, coDeleted INTEGER DEFAULT 0 NOT NULL);
CREATE TABLE IF NOT EXISTS tbTrip (coId INTEGER PRIMARY KEY AUTOINCREMENT, coTripTypeId INTEGER NOT NULL, coTimeOfTrip TEXT NOT NULL, coCustomerId INTEGER NOT NULL, coDescription TEXT null, coDeleted INTEGER DEFAULT 0 NOT NULL);
CREATE TABLE IF NOT EXISTS tbTripType (coId INTEGER PRIMARY KEY AUTOINCREMENT, coName TEXT NOT NULL, coDeleted INTEGER DEFAULT 0 NOT NULL);
CREATE TABLE IF NOT EXISTS tbDatabaseHistory(coId INTEGER PRIMARY KEY AUTOINCREMENT, coTimestamp TEXT NOT NULL, coDescription TEXT NOT NULL);